import React, { useEffect, useState } from "react";
import * as BookAPI from "../BooksAPI";
import "../App.css";

const Book = ({ title, author, shelf, image, id }) => {
  const [updateShelf, setUpdateShelf] = useState("");

  useEffect(() => {
    if (updateShelf) {
      const updating = async () => {
        const res = await BookAPI.update({ id: id }, updateShelf);
        console.log(title, id, updateShelf);
      };
      updating();
    }
  }, [updateShelf]);

  const changeHandler = (e) => {
    setUpdateShelf(e.target.value);
    // onUpdateShelf(true);
  };

  return (
    <div className="book">
      <div className="book-top">
        <div
          className="book-cover"
          style={{
            width: 128,
            height: 192,
            backgroundImage: `url(${image["thumbnail"]})`,
          }}
        ></div>
        <div className="book-shelf-changer">
          {shelf !== "none" && (
            <>
              {console.log("shh :" + shelf)}
              <select
                value={shelf}
                onChange={(e) => setUpdateShelf(e.target.value)}
              >
                <option value="none" disabled>
                  Move to...
                </option>
                <option value="none"> None </option>
                <option value="currentlyReading">Currently Reading</option>
                <option value="wantToRead">Want to Read</option>
                <option value="read">Read</option>
                <option value="none">None</option>
              </select>
            </>
          )}
          {shelf === "none" && (
            <select
              value="none"
              onChange={(e) => setUpdateShelf(e.target.value)}
            >
              <option value="none" disabled>
                Move to...
              </option>
              <option value="none"> None </option>
              <option value="currentlyReading">Currently Reading</option>
              <option value="wantToRead">Want to Read</option>
              <option value="read">Read</option>
              <option value="none">None</option>
            </select>
          )}
        </div>
      </div>
      <div className="book-title">{title}</div>
      <div className="book-authors">{author}</div>
    </div>
  );
};

export default Book;
