import React, { useState } from "react";
import { Link } from "react-router-dom";
import * as BookAPI from "../BooksAPI";
import { useEffect, useRef, useCallback } from "react";
import Book from "./Book";

const SearchBook = () => {
  const [books, setBooks] = useState([]);
  const [shelfBooks, setShelfBooks] = useState([]);
  const [booksearch, setBookSearch] = useState("");
  const searchBookRef = useRef();

  // useEffect(() => {
  //   const BookSearch = async () => {
  //     console.log(searchBookRef.current.value);

  //     const res = await BookAPI.search(searchBookRef.current.value);
  //     // console.log(res);
  //     setBooks(res);
  //     console.log(books);
  //   };
  //   BookSearch();
  // }, [booksearch]);

  const bookSearchCallback = useCallback(() => {
    const BookSearch = async (e) => {
      console.log(e.value);

      const res = await BookAPI.search(e.value);
      // console.log(res);
      setBooks(res);
      console.log(books);
      BookSearch();
    };
  }, [e]);

  useEffect(() => {
    const getBooks = async () => {
      const res = await BookAPI.getAll();
      setShelfBooks(res);
      console.log(res);
    };
    getBooks();
  });

  return (
    <div className="search-books">
      <div className="search-books-bar">
        <Link to="/" className="close-search">
          Close
        </Link>
        <div className="search-books-input-wrapper">
          <input
            type="text"
            placeholder="Search by title, author, or ISBN"
            onChange={(e) => setBookSearch(e.target.value)}
            ref={bookSearchCallback}
          />
        </div>
      </div>
      <div className="search-books-results">
        <ol className="books-grid">
          {console.log(books)}
          {books !== undefined &&
            books.map((b) => {
              let shelf = shelfBooks.filter((f) => f.id === b.id);
              return shelf ? (
                b.imageLinks ? (
                  <Book
                    key={b.id}
                    title={b.title}
                    author={b.authors}
                    shelf={b.shelf}
                    image={b.imageLinks}
                  />
                ) : null
              ) : b.imageLinks ? (
                <Book
                  key={b.id}
                  title={b.title}
                  author={b.authors}
                  shelf={"none"}
                  image={b.imageLinks}
                />
              ) : null;
            })}
        </ol>
      </div>
    </div>
  );
};

export default SearchBook;
