import "./App.css";
import React, { useEffect, useState } from "react";
import { Route, Routes } from "react-router-dom";
import BookShelf from "./components/BookShelf";
import SearchBook from "./components/SearchBook";
import * as BookAPI from "./BooksAPI";
import TitleBar from "./components/TitleBar";

function App() {
  const [shelfBooks, setShelfBooks] = useState([]);
  // const [ContinueReading, setContinueReading] = useState([]);
  // const [WantToRead, setWantToRead] = useState([]);
  // const [Readd, setRead] = useState([]);

  useEffect(() => {
    const getBooks = async () => {
      const res = await BookAPI.getAll();
      setShelfBooks(res);
    };
    getBooks();
  }, []);

  let CR = shelfBooks.filter((f) => f.shelf === "currentlyReading");
  let WTR = shelfBooks.filter((f) => f.shelf === "wantToRead");
  let Read = shelfBooks.filter((f) => f.shelf === "read");

  return (
    <div className="app">
      <Routes>
        <Route
          exact
          path="/"
          element={
            <>
              <TitleBar />
              <BookShelf CurrentShelf={CR} title="Continue Reading" />
              <BookShelf CurrentShelf={WTR} title="Want to Read" />
              <BookShelf CurrentShelf={Read} title="Read" />
            </>
          }
        ></Route>

        <Route path="/search" element={<SearchBook />}></Route>
      </Routes>
    </div>
  );
}

export default App;
