import React, { useEffect, useState } from "react";
import * as BookAPI from "../BooksAPI";
import "../App.css";

const Book = ({ key, title, author, shelf, image }) => {
  const [updateShelf, setUpdateShelf] = useState("");

  useEffect(() => {
    if (updateShelf) {
      const updating = async () => {
        const res = await BookAPI.update({ id: key }, updateShelf);
        console.log(res);
      };
      updating();
    }
  }, [updateShelf]);

  const changeHandler = (e) => {
    setUpdateShelf(e.target.value);
    console.log("new shelf:" + updateShelf);
  };
  return (
    <div className="book">
      <div className="book-top">
        <div
          className="book-cover"
          style={{
            width: 128,
            height: 192,
            backgroundImage: `url(${image["thumbnail"]})`,
          }}
        ></div>
        <div className="book-shelf-changer">
          {shelf !== "none" && (
            <>
              {console.log("shh :" + shelf)}
              <select value={shelf} onChange={(e) => changeHandler(e)}>
                <option value="none" disabled>
                  Move to...
                </option>
                <option value="none"> None </option>
                <option value="currentlyReading">Currently Reading</option>
                <option value="wantToRead">Want to Read</option>
                <option value="read">Read</option>
                <option value="none">None</option>
              </select>
            </>
          )}
          {shelf === "none" && (
            <select value="none" onChange={(e) => changeHandler}>
              <option value="none" disabled>
                Move to...
              </option>
              <option value="none"> None </option>
              <option value="currentlyReading">Currently Reading</option>
              <option value="wantToRead">Want to Read</option>
              <option value="read">Read</option>
              <option value="none">None</option>
            </select>
          )}
        </div>
      </div>
      <div className="book-title">{title}</div>
      <div className="book-authors">{author}</div>
    </div>
  );
};

export default Book;
